if ("undefined" == typeof jQuery) throw new Error("This application requires jQuery");
var APP = {
    init: function () {
        $("html").Core({
            console: !1,
            loader: !1
        })
    },
    sidebarSparklines: {
        init: function () {
            $("aside .sidebar-sparklines").sparkline("html", {
                enableTagOptions: !0
            })
        }
    }
};
$(function () {
    APP.init(), APP.sidebarSparklines.init()
});